import React, {useContext} from 'react'
import {NavLink} from 'react-router-dom'
import {CurentUserContext} from 'contexts/curentUserContaxet' 

const FeedToggler = ({tagName}) => {
  const [curentUserState]  = useContext(CurentUserContext)

  return (
    <div className="feed-toggler">
      <ul className="nav nav-pills outline-active">
        {curentUserState.currentUser && (
          <li className="nav-item">
            <NavLink to="/feed" className="nav-link">
             Your feed
            </NavLink>
          </li>
        )}
        
        <li className="nav-item">
          <NavLink to="/" className="nav-link" exact>
            Global feed
          </NavLink>
        </li>
        {tagName && (
          <li className="nav-item">
            <NavLink to={`/tags/${tagName}`} className="nav-link" exact>
              <i className="ion-pound"></i>
              #{tagName}
            </NavLink>
          </li>
        )}
      </ul>
    </div>
  )
}

export default FeedToggler
