import React,{createContext, useState} from 'react'


export const CurentUserContext = createContext([{}, ()=>{}])

export const CurentUserProvider = ({children}) => {
    const [state, setState] = useState({
        isLoading: false,
        isLoggedIn: null,
        currentUser: null
    })
    return (
            <CurentUserContext.Provider value={[state, setState]}>
                {children}
            </CurentUserContext.Provider>
            )
}