import React, {useState} from 'react'
import {Link} from 'react-router-dom'
import useFetch from 'customHooks/useFetch'

const Autentification = (props) => {
const isLogin = props.location.pathname === '/login'
const pageTitle = isLogin ? 'Войти' : 'Зарегистрироваться'
const descriptionLink = isLogin ? '/registration' : '/login'
const descriptionText = isLogin ? 'Нужен акаунт?' : 'Есть акаунт?'
const apiUrl = isLogin ? '/users/login' : '/users'

const [email, setEmail] = useState('')
const [password, setPassword] = useState('')
const [username, setUserName] = useState('')
// eslint-disable-next-line
const [{isloading, response, error}, doFetch] = useFetch(apiUrl)

function submitHandler (e) {
  e.preventDefault()
  console.log('Форма ушла');
  const user = isLogin ? {email, password} : {email, password, username}
  doFetch({
    method: "post",
    data:{
      user
    }
  })
}




  return (
    <div className="auth-page">
      
      <div className="container page">
        <div className="row">
          <div className="col-md-6 offset-md-3 col-xs-12">
            <h1 className="text-xs-center">{pageTitle}</h1>
            <p className="text-xs-center">
              <Link to={descriptionLink}>{descriptionText}</Link>
            </p>
            <form onSubmit={submitHandler}>
              <fieldset>
                {
                  !isLogin && 
                  <fieldset className="form-group">
                    <input
                      type="text"
                      className="form-control form-control-lg"
                      autoComplete="on"
                      placeholder="Username"
                      value= {username}
                      onChange={e => {
                          setUserName(e.target.value)
                      }}
                    />
                  </fieldset>
                }
                <fieldset className="form-group">
                  <input
                    type="email"
                    className="form-control form-control-lg"
                    autoComplete="on"
                    placeholder="Email"
                    value= {email}
                    onChange={e => {
                        setEmail(e.target.value)
                    }}
                  />
                </fieldset>
                <fieldset className="form-group">
                  <input
                    type="password"
                    className="form-control form-control-lg"
                    placeholder="Password"
                    autoComplete="on"
                    value= {password}
                    onChange={e => {
                        setPassword(e.target.value)
                    }}
                  />
                </fieldset>
                <button
                  className="btn btn-lg btn-primary pull-xs-right"
                  type="submit"
                  disabled={isloading}
                >
                  {pageTitle}
                </button>
              </fieldset>
            </form>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Autentification
